package clases;

public class MetodosYVariables {

	static int entero=5;
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub

	
		System.out.println(entero);
		
		incrementador();
		
		System.out.println(entero);
	}
	
	public static void incrementador() {
		
		entero++;
		
	}
	


}
